INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (1, 'O Mago', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F30312D4F2D4D61676F2E6A7067, 'KETHER - O Ancião dos dias', 'Aleph', 'Iniciação nos mistérios', 'Poder volitivo', 'Domínio das forças em movimento', 'Sê em tuas obras como és em teus pensamentos', 'Mercúrio', 'Obstáculos vencidos; iniciativas positivas; influências variáveis.', 'Vontade, virtude, perseverança, triunfo ante o adverso, impõem-se os sãos ideais.', 'Sabedoria, talento, genialidade. Dúvidas e demoras. Assinala ao consultante e ao gênio, se este Tarot está junto ao nº 77.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (2, 'A Sacerdotisa', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F30322D412D5361636572646F746973612E6A7067, 'CHOKMAH: o Cristo Cósmico. Ciência Oculta.', 'Beth. Ciência oculta. Favorável.', 'Manancial de Vida', 'Jogo de Opostos', 'Intercâmbio, duplicidade.', 'O vento e as ondas vão sempre a favor de quem sabe navegar.', 'Virgem', 'A verdade se impõe; a intuição não engana; prudência na generosidade indiscriminada.', 'Clareza conceitual, lucidez, critério analítico, capacidade de observação. Engenho para concertar negócios complicados.', 'Paixão envolvente, exaltação espiritual, rixas, ira, amarguras, dissensões. Junto com os tarôs nº 21, 63 e 77: Traição.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (3, 'A Imperatriz', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F30332D412D496D706572617472697A2E6A7067, 'BINAH: O Espírito Santo. Mãe Celeste.', 'Gimel. Produção Material e Espiritual.', 'Conhecimento do oculto.', 'Ideação', 'Expansão de ideias e desejos.', 'Tecendo está teu tear, telas para teu uso e telas que não hás de usar', 'Libra', 'Possível matrimônio, a dúvida é carcoma da alma; aproveite a oportunidade.', 'Conúbio certo; união sentimental duradoura; atração recíproca; beijos castos.', 'Ruptura; dissensões, altercadas; separação.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (4, 'O Imperador', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F30342D4F2D496D70657261646F722E6A7067, 'CHESED: O Íntimo. O Real Ser. Mando.', 'Daleth. Progresso. Êxito. Misericórdia.', 'Manifestação da virtude divina.', 'Solução final', 'Realização de coisas materiais.', 'Ao trabalho de tuas mãos, dá benção e, no do pensamento, põe coração', 'Escorpião', 'Contatos afetivos intensos; controle do material; domínio de si mesmo.', 'Erotismo; filhos; elevação espiritual; Deus.', 'Distanciamentos; frieza afetiva; desconcerto; carta de despedida.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (5, 'O Hierarca', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F30352D4F2D48696572617263612E6A7067, 'GEBURAH: Alma Espiritual. A Lei.', 'He. O Carma. Marte. Guerra.', 'Manifestações do Criador.', 'Domínio das paixões.', 'Controle das Forças Naturais.', 'De ouvidos te havia ouvido, mas agora meus olhos te veem e meu coração te sente.', 'Júpiter', 'Cooperação inesperada; ajuda de iguais ou superiores; critério sanamente orientado.', 'Mão amiga e pródiga; diversão excelente; truque satisfatório; emoção ante um encontro.', 'Retração; dilação; nostalgias persistentes; isolamento.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (6, 'A Indecisão', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F30362D412D496E6465636973616F2E6A7067, 'TIPHERETH: A Alma Humana. O Enamorado.', 'Vau. Vitória. Boa Sorte.', 'Conhecimento instintivo', 'Abstinência e Gula, Liberdade e Necessidade, Dever e Direito.', 'Determinação de estado.', 'Trabalhos me dás, Senhor, mas com eles, fortaleza.', 'Vênus', 'Não se deixe tentar; fixe suas posições; guie-se pelo espiritual.', 'Devaneios constantes; beco sem saída; abatimento; desgostos.', 'Invejas à espreita; armadilhas importunas; confianças traiçoeiras; amigos enganosos; intrigueiros.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (7, 'O Triunfo', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F30372D4F2D547269756E666F2E6A7067, 'NETZACH: Corpo Mental. Guerra. Lutas.', 'Zain. Expiação. Dor. Amarguras.', 'O espírito governa a matéria.', 'Eliminação de dúvidas e erros.', 'Impulsos e desejos de superação.', 'Quando a ciência entre em teu coração e a sabedoria seja doce a tua alma, pede e te será dado.', 'Sagitário', 'Triunfo no que se propõe; seu esforço encontra recompensa; férias agradáveis.', 'Operação vantajosa; contrato seguro; escritos aceitos; projeto concreto.', 'Perda de objeto valioso; lamentações inúteis; palavras de consolo de desconhecidas; ardil na casualidade.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (8, 'A Justiça', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F30382D412D4A7573746963612E6A7067, 'HOD: Corpo Astral. O Número de Jó. Mercúrio.', 'Cheth. Sofrimentos. Provas. Dor.', 'O Absoluto', 'A força do direito.', 'Evolução e involução, atração e rechaço.', 'Edifica um altar em teu coração, mas não faças de teu coração um altar', 'Capricórnio', 'Modere seus ímpetos e desejos; não tome decisões por hoje; relaxe-se.', 'Achado produtivo; seu número: o 6; poupe fadigas perguntando; teimosia vencida a seu favor.', 'Resolução imprudente; amanhã é tarde; não divague; recordações exumadas que atormentam.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (9, 'O Eremita', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F30392D4F2D4572656D6974612E6A7067, 'JESOD: Corpo etérico. O Ermitão.', 'Teth. Solidão. Sofrimento.', 'A luz divina nas obras humanas.', 'Discrição; caridade e conhecimento.', 'Concreção, culminação e elevação de anelos.', 'Sobe ao monte e contempla a terra prometida; mas não te digo que entrarás nela.', 'Aquário', 'Afirme sua discrição; não comente seus planos de compra; medite sobre a oportunidade de seu anelo.', 'Uma palavra a mais que o desprestigia; versões encontradas; ausência de equilíbrio interior; murmurações.', 'Sacerdote, pastor, rabino. Confiança em um segredo que não se revela; mutismo benéfico.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (10, 'A Retribuição', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F31302D412D526574726962756963616F2E6A7067, 'MALCHUTH: Corpo Físico. A Roda da Fortuna.', 'Iod. Bons negócios. Mudanças.', 'Tempo e circunstâncias, artífices da perfeição.', 'Os diversos processos do pensamento.', 'Ação e reação.', 'Custoso é o saber que compras com a experiência e mais custoso o que te falta comprar.', 'Urano', 'O insólito enfrenta à fortuna; adeus a uma velha amizade; mal humor e aflição. Não desespere.', 'Um amigo da alma se afasta; velhos camaradas golpeiam à porta; carta anunciando presente; conhecimento de um segredo ansiado.', 'Perdeu a oportunidade por agora; não concorra a este encontro; reconheça a verdade ainda que te doa; angústias.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (11, 'A Persuasão', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F31312D412D50657273756173616F2E6A7067, 'O LEÃO DOMADO. Favorece a Lei. Marte.', 'Khaph. Que não haja temor.', 'O espiritual é princípio precursor.', 'O domínio através do intelecto.', 'Governo das baixas paixões.', 'Gozoso na esperança, sofrido na atribulação, sê constante na oração.', 'Netuno', 'Sua intuição não falha; agigante sua fé; o período de confusão será breve; há um compasso de espera.', 'Apresse o trâmite; confirme suas suspeitas; não adote ilusões caprichosas.', 'Desolação por esquecimento; sepulte a ambição; suas ambiguidades não o beneficiam.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (12, 'O Apostolado', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F31322D4F2D41706F73746F6C61646F2E6A7067, 'O SACRIFÍCIO. Provas e dor.', 'Lamed. Arcano A.Z.F. nos tira da dor.', 'Complacência no bem realizado.', 'Circunspecção em decisões dolorosas.', 'A abnegação como força expansiva.', 'Ainda que o sol te fadigue de dia e a lua te entristeça de noite, não leves teus pés ao resvaladeiro nem durmas quando faz guarda.', 'Peixes', 'Antigas amarguras se dissipam; os rodeios não se enquadram quando deve falar o coração; deleite-se no belo e aperfeiçoará sua alma.', 'Reúna amizade e recolherá sorrisos; não ameace fugas, afronte os fatos; a amabilidade deve ser parte de sua própria complacência.', 'Amenize sua irritação; admoestação próxima; amigo perturbador prejudica o sarau.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (13, 'A Imortalidade', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F31332D412D496D6F7274616C69646164652E6A7067, 'MORTE E RESSURREIÇÃO. Transformações.', 'Mem. Indica Mudança Total.', 'Renovação da vida.', 'A inércia como função do movimento.', 'Letargia, sonolência, sonambulismo.', 'A noite passou e chegou o novo dia. Veste-te, pois, com as armas da luz.', 'Áries', 'Ânsias favorecidas pela distância; não se entregue ao abandono; cuide sua prestância; renove seus pontos de vista e rejuvenescerá.', 'A pomposidade não coincide com você; não condene por analogia; o amor não sabe de frases ribombantes, senão de simples entrega.', 'Rixa por assunto de interesses; choque; extremada preguiça que tudo complica.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (14, 'A Temperança', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F31342D412D54656D706572616E63612E6A7067, 'MATRIMÔNIO. ASSOCIAÇÃO. Longa Vida.', 'Num. Estabilização. Mal para Mudança.', 'União do interior com o exterior.', 'Afetos recíprocos, associação de ideias.', 'Regulação das relações dos sexos, equilíbrio da força vital.', 'Não sejas como palha diante do vento, nem como vento diante da palha.', 'Touro', 'Convém acordar às coisas sua justa proporção; o exagero destrói a virtude; o equilíbrio é base da paz espiritual.', 'Auxilie-se suavizando desinteligências; concretize seu afã decididamente, porém sem danar o próximo; a hostilidade cederá.', 'Excessos em comidas e bebidas; o médico chama; não viva nos extremos; extraia do aparentemente incoerente seu fundo de verdade.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (15, 'A Paixão', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F31352D412D50616978616F2E6A7067, 'TIPHON BAFOMETO. Fracasso Amoroso.', 'Samech. Anuncia perigos.', 'Manifestação da vontade individual.', 'A força do desejo, o impulso até o oposto.', 'Processos gerativos, desejos intensos.', 'Fizeram-me guarda de vinhas e minha vinha, que era minha, não guardei.', 'Saturno', 'A vontade individual é fator determinante; o atrativo do mistério seduz, porém destrói sem guia; a criação é fogo ardente que não há de deixar-se extinguir.', 'Prosperidade por vias legais e por fatalidade; ânsias veementes; paixões violentas.', 'Afetos nocivos; situações de violência; discórdias; malícia.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (16, 'A Fragilidade', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F31362D412D46726167696C69646164652E6A7067, 'A TORRE FULMINADA. Castigo. Queda Terrível.', 'Ain. Evite esta data.', 'A aflição desperta a virtude.', 'A nulidade dos valores materiais.', 'Aflição, rigores, severidade.', 'Luz de amanhecer, luz de meio-dia, luz de anoitecer, o que importa é que seja luz.', 'Marte', 'O valor do temporal se exalta em função do eterno; o perecedouro tem vital importância no devir das coisas.', 'Reciprocidade no amor e no ódio, na indiferença e no zelo, na traição e na lealdade.', 'Acidente imprevisto; morte; necessidade; tempestades.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (17, 'A Esperança', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F31372D412D4573706572616E63612E6A7067, 'A ESTRELA DA ESPERANÇA. Espera.', 'Phe. Significa esperança e espera.', 'A abnegação como manancial de vida.', 'A experiência é luz.', 'Otimismo, expectativas e caridade.', 'Uns homens pedem sinais para crer e outros pedem sabedoria para obrar, mas o coração esperançoso tem tudo em suas esperanças.', 'Gêmeos', 'Aguardar o futuro confiadamente é pregustar a imortalidade da existência. A magia da fé cria a tudo.', 'Intuição, iluminação, nascimentos, ganhos.', 'Breves aflições e breves satisfações; enfados e reconciliações; privações, abandonos.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (18, 'O Crepusculo', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F31382D4F2D43726570757363756C6F2E6A7067, 'INIMIGOS OCULTOS: Saltam a qualquer momento.', 'Tzade. Enfermidades. Mal para negócios.', 'O mistério da vida.', 'O negativo é expoente do positivo.', 'Manifestação de poderes ocultos.', 'Seja tua caridade celeiro inesgotável e tua paciência não menos inesgotável que tua caridade.', 'Câncer', 'A contrariedade e o engano espreitam; sua hostilidade fomenta inimizades; cuidado com as adulações traiçoeiras.', 'Triunfos e fracassos aparentes.', 'Inconstância, ciladas, confusão, mudanças, largas deliberações, impedimentos inesperados, resultados tardios.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (19, 'A Inspiração', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F31392D412D496E737069726163616F2E6A7067, 'SOL RADIANTE. Êxitos. Soa Sorte.', 'Coph. A Pedra Filosofal.', 'A luz divina é princípio do conhecimento.', 'A inteligência motora.', 'Materialização das ideias em atos.', 'Toma o escudo de tua fé e avança com passo decidido, já seja a favor do vento ou contra todos os ventos.', 'Leão', 'A alegria se alberga em sua temperança; consagre-se aos seus e não te pesará; o amor puro é sua couraça.', 'Aumento de poder, benefícios por esforço próprio, heranças, claridade no que se deseja.', 'Perigo no emprego, disputas por escritos, desinteligências por resultados.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (20, 'A Ressureição', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F32302D412D526573737572656963616F2E6A7067, 'MUDANÇAS FAVORÁVEIS. Aproveite-as.', 'Resch. Acabar com as debilidades.', 'Iluminação interior.', 'Revelação da genialidade.', 'Harmônica correspondência entre o moral e o material.', 'Flor na macieira, fruto na vinha, semeado em madureza.', 'Lua', 'Velhas aspirações postergadas se concretam; desperte à realidade; preguiça não ajuda a ressurgir.', 'Eleições harmônicas, iniciativas afortunadas, compensações, amigos fiéis.', 'Ganhos que se postergam; amigos traidores que socavam o êxito.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (21, 'A Transmutação', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F32312D412D5472616E736D75746163616F2E6A7067, 'O LOUCO. A INSENSATEZ. Desmoralização total para o mal. Chave mágica: Runa olin.', 'Shin. Antíteses, inimigos de Hiram Habif', 'Inescrutabilidade das leis divinas.', 'Credulidade, ousadia e temeridade são causa de ignorância, erro e perigo.', 'Imprudência, extravagância, delírio, vaidade.', 'Em seu segredo, não entra minha alma, nem em seu porto meu navio.', 'Plutão', 'Não dê passos em falso por indiscrição; se duvida muito, autolimitar-se-á; ajude ao próximo.', 'Camponês; fim de umas coisas e começo de outras.', 'Perigo de isolamento; ofuscação; presente pérfido; promessa enganosa; desilusão.');
INSERT INTO enochian_db.arcane_major (id, name, image, description, letter, spiritual_plane, mental_plane, physical_plane, transcendent_axiom, astrological_association, in_general, favorable, not_favorable) VALUES (22, 'O Regresso', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F32322D4F2D526567726573736F2E6A7067, 'A COROA DA VIDA. Verdade. Vitória. Triunfo. Tudo sai bem.', 'Thau. Poder. Força. Boa sorte.', 'A potestade de manifestar a vida em infinitas formas.', 'O conhecimento supremo é bem-aventurança.', 'Inspiração, trabalho e recompensa convenientes.', 'Sai o sol e põe-se o sol, e outra vez volta a seu lugar, onde torna a nascer.', 'Sol', 'O êxito está ao alcance de sua mão; na amizade está seu maior apoio; use a imaginação, não se submeta a ela.', 'Longa vida; herança, vitórias; aptidão para vencer obstáculos.', 'Rivais que disputam os afetos, situação incerta. Déspota, tirano, familiar dominante.');