INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (23, 'O Lavrador', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F32332D4F2D4C61767261646F722E6A7067, 'Inteligência elementar em seu labor de conhecer e aproveitar os frutos da experiência.', '“Moa, meu moinho, farinha para mim e farinha para o vizinho“.', 'Mercúrio, letra T, número 5.', 'Concentre-se em seu labor; fadigar-se pelo justo é virtude; não desperdice sua experiência.', 'Camponesa; amigo poderoso; elevação por obra deste amigo e da própria vontade.', 'Mulher bondosa; breve contrariedade.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (24, 'A Tecelã', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F32342D412D546563656C612E6A7067, 'Inteligência elementar em seu labor de dar aplicação aos frutos do aprendido.', '“Malha após malha tece meu tear, telas para minha honra e telas para honrar”.', 'Vênus, letra U, número 6.', 'Mulher, sua diligência tem prêmio; honra e lar são uma só coisa; seja inteligente em prodigar esforços.', 'Economia organizada; castidade; proteção feminina, bom desempenho das obrigações.', 'Desassossego interior; dúvida de consciência.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (25, 'O Argonauta', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F32352D4F2D4172676F6E617574612E6A7067, 'O homem animoso que afronta os perigos do desconhecido.', '“Navega, minha barca, navega à porfia; navega de noite, navega de dia”.', 'Netuno, letra V, número 7.', 'O desconhecido implica perigos; preste atenção aos conselhos; não se isole demais.', 'Estrangeiro, estrangeira.', 'Ausências, emigrações, abandonos, mudanças, querela doméstica; algo que se adquire e muito que se perde.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (26, 'O Prodígio', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F32362D4F2D50726F646967696F2E6A7067, 'A ação do tempo como justiça e poder de manifestação.', '“Foi tempo de romper e é tempo de coser; tempo foi de falar e é tempo de calar”.', 'Saturno, letra X, número 8.', 'O travesseiro é bom conselheiro; não tema o inesperado; siga a guia de seu subconsciente.', 'Feito surpreendente, ensinamento impensado.', 'Perigo que ameaça.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (27, 'O Inesperado', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F32372D4F2D496E657370657261646F2E6A7067, 'A vida interior como causa determinante da exterior.', '“Nem excesso de mel para adoçar nem vanglória para prosperar”.', 'Marte, letra Y, número 9.', 'Busque o maravilhoso sem submeter-se a fantasias; o universo é a maravilha de Deus; aprenda a descobrir o surpreendente no cotidiano.', 'Triunfo condicionado, surpresa, achado, descoberta tardia.', ' Conjuração, traição.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (28, 'A Incerteza', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F32382D412D496E63657274657A612E6A7067, 'O juízo como causa determinante dos atos.', '“Não busques em outros o que está em ti; nem busques em ti o que está em outros”.', 'Plutão, letra Z, número 1.', 'rejeite “o que dirão”; resolva-se sem múltiplas consultas; ceia em si mesmo.', 'Mistério a resolver e ciência para resolvê-lo.', 'Demora, obstáculo, contrariedade.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (29, 'A Domesticidade', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F32392D412D446F6D657374696369646164652E6A7067, 'A paz da natureza, o equilíbrio dos elementos, a alegria bucólica.', '“Olhos de juventude sejam teus olhos; e tua palavra, prudência de ancião seja”.', 'Lua, letra B, número 2.', 'Modere suas palavras; convença com persuasão e doçura; não se irrite ante quem não escuta.', 'Vida campestre, negócio vantajoso, empresa que traz luta, mas, também, recompensa.', 'Remordimento, indecisão, perplexidade, timidez.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (30, 'O Intercâmbio', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F33302D4F2D496E74657263616D62696F2E6A7067, 'A expansão individual por meio da convivência comercial.', '“Ceifa tua terra com esmero; mas não cultives teu campo com avareza”.', 'Júpiter, letra C, número 3.', 'Seja sociável com todos; no intercâmbio está o ganho; enclausurar-se é anular-se.', 'Serventes; discussão sem solução.', 'Vida social, intercomunicações, comércio, tráfego, variedade.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (31, 'Os Impedimentos', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F33312D4F732D496D706564696D656E746F732E6A7067, 'O princípio da depuração como elemento de progresso.', '“Não faças juízo sem misericórdia; nem tenhas misericórdia sem juízo”.', 'Urano, letra CH, número 4.', 'As dificuldades são trampolins até o logro; as forças opostas também podem ser positivas; não desanime.', 'Expectativas, promessas, artefatos domésticos.', 'Restrições, trato com pessoas de classe inferior com prejuízo recíproco.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (32, 'A Magnificência', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F33322D412D4D61676E69666963656E6369612E6A7067, 'O princípio da ostentação como causa de discussão.', '“Esgota os recursos de tua inteligência; mas não esgotes os de teu coração”.', 'Mercúrio, letra D, número 5.', 'Fazer-se valer é respeitar-se; defenda seus direitos, mas cumpra com suas obrigações.', 'Cooperação entre pessoas de igual hierarquia.', 'Litígio, loquacidade, excesso de cólera, fracasso.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (33, 'A Aliança', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F33332D412D416C69616E63612E6A7067, 'O princípio da própria realização por associação.', '“Alegra-te com o amor de tua mocidade; e alegra-te com o amor de tua maturidade”.', 'Vênus, letra E, número 6.', 'Uma sua vontade à dos demais; a afinidade espiritual deve modelar-se; identifique-se com os triunfos alheios e reconhecerão os seus próprios.', 'Pactos acertados, aliança duradoura, aumento de prosperidade, contingências felizes, progresso.', 'Mau investimento, perda de documentos.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (34, 'A Inovação', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F33342D412D496E6F766163616F2E6A7067, 'O princípio da própria inspiração como guia das atividades.', '“Longevidade há em tua mão direita; e na esquerda, trabalhos e honra”.', 'Netuno, letra F, número 7.', 'Trabalhe ordenadamente; o esforço dispersado não dá fruto; evoluir significa dor e fadiga.', 'Invenção, nova empresa, achado afortunado.', 'Tentação, temeridade, achado desconcertante.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (35, 'O Desconsolo', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F33352D4F2D446573636F6E736F6C6F2E6A7067, 'O princípio do conhecimento da própria insuficiência.', '“Depois de agora, vem o que foi antes; e antes foi o que será agora”.', 'Saturno, letra G, número 8.', 'Suporte a aflição com fé; não reprima suas lágrimas; fortaleça seu espírito.', 'Acontecimento imprevisto.', 'Alarme, consternação, melancolia, tristeza, obstáculos.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (36, 'A Iniciação', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F33362D412D496E6963696163616F2E6A7067, 'O princípio de desdobrar das próprias virtudes.', '“Doce é o tanger, doce o cantar, doce o escutar”.', 'Marte, letra H, número 9.', 'Não empreenda nada ao azar; reconheça que sempre há alguém mais sábio que você; excute a advertência de quem sabe.', 'Nascimento, novo começo, primícias, fortuna próxima, êxito nos negócios.', 'Dissipação, declinação, decadência.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (37, 'A Arte e a Ciência', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F33372D412D417274652D652D412D4369656E6369612E6A7067, 'O princípio da criação por meio do conhecimento aplicado.', '“Terra de promissão ante meus olhos; ajuda-me, pé, a chegar a ela”.', 'Sol, letra I, número 1.', 'Combine o prático com o espiritual; amar a arte é amar a vida; a ciência é um milagre que sempre se renova.', 'Mulher loira; amizade sincera, ajuda magnânima, probidade, recursos engenhosos, popularidade.', 'Compulsões, perversões, fama não muito edificante. Mulher casada.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (38, 'A Duplicidade', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F33382D412D4475706C696369646164652E6A7067, 'O princípio dos antônimos como elemento de comparação e seleção.', '“Mulher virtuosa, nem todos os que veem suas obras, veem suas virtudes”.', 'Lua, letra J, número 2.', 'Aplique sua sagacidade para o bem; jamais prejulgue a seu próximo; a animosidade é má conselheira.', 'Influência de mulheres ao resolver assuntos com sabedoria, virtude e honradez.', 'Influência de mulheres ao resolver assuntos com torpeza, escândalo e corrupção.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (39, 'O Testemunho', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F33392D4F2D54657374656D756E686F2E6A7067, 'O princípio de demonstração como elemento de convicção.', '“Põe tuas intenções como testemunho ante ti mesmo; mas, ante os demais, põe como testemunho tuas obras”.', 'Júpiter, letra K, número 3.', 'Certifique-se antes de decidir; meça as consequências; não dê crédito a comentários furtivos.', 'Aproximações, convites, viagens, matrimônio tardio.', 'Jovem loira; adultério presumido ou real.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (40, 'O Presentimento', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F34302D4F2D5072657373656E74696D656E746F2E6A7067, 'O princípio da presciência como faculdade natural.', '“Não sejas varão de olhos abertos à cobiça; nem mulher de ouvido aberto à lisonja”.', 'Urano, letra L, número 4.', 'Tome seus pressentimentos com pinças; nem todo o aflitivo se concreta; sempre há um renovo de esperança.', 'Trabalho bem remunerado; simpatia, sedução, reflexão.', 'Ameaça de ruína, afetos contrariados.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (41, 'O Desassossego', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F34312D4F2D4465736173736F737365676F2E6A7067, 'O princípio da vida cidadã.', '“Os arcos dos fortes foram quebrados; e os débeis cingiram-se de fortaleza”.', 'Mercúrio, letra LL, número 5.', 'Inquietudes emergentes; o desassossego conspiração contra você; debilidade emocional.', 'Família numerosa, amor de juventude.', 'Indignação, violência, multidão em movimento.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (42, 'A Preeminência', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F34322D412D507265656D696E656E6369612E6A7067, 'O princípio do poder e obediência voluntária.', '“Principal em dignidade, principal em poder, sê principal em mérito”.', 'Vênus, letra M, número 6.', 'Acate a hierarquia sem humilhar-se; obedeça racionalmente; o respeito é índice de sã tolerância.', 'Vantagens, triunfos, superioridade. Amores surpreendentes.', 'Jovem ruiva.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (43, 'A Alucinação', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F34332D412D416C7563696E6163616F2E6A7067, 'O princípio criador das ideias induzidas.', '“Alegria de coração, embeleza meu rosto”.', 'Netuno, letra N, número 7.', 'Regozijo inesperado; o riso é um remédio universal; sorria amiúde.', 'Satisfação, alegria, pudor, modéstia, negócio próspero.', 'Paixão violenta, amor proibido, divórcio.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (44, 'O Pensamento', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F34342D4F2D50656E73616D656E746F2E6A7067, 'O princípio da autoedificação.', '”Sobrepuja com sabedoria os receios e goza do que é teu em esperança”.', 'Saturno, letra Ñ, número 8.', 'Ordene seus pensamentos; cultivar-se é progredir; medite sobre seus atos.', 'Projeto, resolução, determinação.', 'Logro do que se deseja, após muita luta, por meio de uma pessoa do sexo oposto.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (45, 'A Regeneração', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F34352D412D526567656E65726163616F2E6A7067, 'O princípio da memorização e visualização.', '“Tudo é formoso em seu tempo e tudo é saboroso em sua época”.', 'Marte, letra O, número 9.', 'Utilize seu passado como cimento de seu presente; não se abata pelos erros; supere-os, remediando-os.', 'Influência de parentes em assuntos amorosos.', 'Rupturas e abandono.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (46, 'O Patrimônio', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F34362D4F2D50617472696D6F6E696F2E6A7067, 'O princípio da prossecução natural.', '“O contentamento no pouco abre as portas do muito”.', 'Sol, letra P, número 1.', 'Herança próxima; benefícios econômicos; possível esbanjamento.', 'Legado, doação, afinidade conjugal.', 'Para um homem: perigo por uma mulher; para uma mulher: perigo de sedução.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (47, 'A Conjectura', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F34372D412D436F6E6A6563747572612E6A7067, 'O princípio de dedução como elemento de iluminação.', '“Ser resplendor na luz e calor no fogo é tua missão por agora e para logo”.', 'Lua, letra Q, número 2.', 'Suas conjecturas são certas; não se descontrole; confirme o fato de que, falando, não prejudicará.', 'Novos ensinamentos; alegria próxima; apoio de parentes; amor virtuoso e durável.', 'Aflições espirituais; pena próxima.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (48, 'A Consumação', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F34382D412D436F6E73756D6163616F2E6A7067, 'O princípio da determinação da conduta.', '“Bebe a gota de tua cisterna, ou os caldais de teu poço”.', 'Júpiter, letra R, número 3.', 'Segurança em si mesmo; plenitude de satisfações; convencimento do próprio valor. Cuidado com a vaidade!', 'Logro, vitória, ciência.', 'Amor impossível.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (49, 'A Versatilidade', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F34392D412D566572736174696C69646164652E6A7067, 'O princípio da permuta como elemento da prosperidade.', '“Doce é o trabalho de quem trabalha contente; e doce o descanso de quem o tem merecido”.', 'Urano, letra S, número 4.', 'Equilibre seu sentido de correspondência; descarte o interesse; a reciprocidade é virtude.', 'Mudanças, novos afetos.', 'Rivalidade em amores; vicissitudes, algumas para o bem.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (50, 'A Afinidade', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F35302D412D4166696E69646164652E6A7067, 'O princípio do estímulo natural como poder modelador dos atos.', '“Tição és no incêndio, mulher; e tu, homem, vento que aviva o fogo”.', 'Mercúrio, letra T, número 5.', 'Desperta atrativos; pode haver litígios; contato amoroso.', 'Homem malvado; ilusões, amor, paixões.', 'Alucinações, temeridade, perigos que se ignoram. Advogado, escrivão.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (51, 'O Assessoramento', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F35312D4F2D4173736573736F72616D656E746F2E6A7067, 'O princípio do respeito à ordem estabelecida.', '“Palavra de prata é o conselho do sábio; e palavra de ouro, a de quem o busca e o segue”.', 'Vênus, letra U, número 6.', 'Siga o conselho; seu saber é limitado, porém, válido; confie mais no próximo.', 'Viúva; relação com pessoas de autoridade; fortuna na carreira das armas ou da magistratura.', 'Mulher malvada; assuntos judiciais; inimigos poderosos; perigo de perfídias.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (52, 'A Premeditação', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F35322D412D5072656D656469746163616F2E6A7067, 'O princípio da avaliação de fatores.', '“Põe teu coração em todas as tuas palavras; mas não ponhas todas as palavras em teu coração”.', 'Netuno, letra V, número 7.', 'Seu triunfo depende da previsão; atue sem pressa, porém, sem pausa; acordo de vontades.', 'Militar, polícia, gendarme; lutas, ataques.', 'Finezas fingidas, dissimulações, indigência; ódios femininos; lutas como consequência destes ódios.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (53, 'O Ressentimento', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F35332D4F2D52657373656E74696D656E746F2E6A7067, 'O princípio da represália.', '“Não brandas espada vingadora nem temas espada reparadora”.', 'Saturno, letra X, número 8.', 'Reveja sua conduta e achará a origem de seus agravos; seu ressentimento não é saudável.', 'É preciso prevenir-se.', 'Inaptidão, ataques, críticas, calúnias, perigo pelo fogo, lutas com pessoas de posição.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (54, 'O Exame', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F35342D4F2D4578616D652E6A7067, 'O princípio do livre debate.', '“Examina tua vinha e recolhe os grãos caídos; mas não procures rancores nem recolhas insultos”.', 'Marte, letra Y, número 9.', 'Seja justo em pensamento, palavra e obra; a equanimidade não abunda; podem prejudica-lo.', 'Espião; especulações e indagações.', 'Imputações, traições; inimigos ocultos; difamação.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (55, 'A Contrição', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F35352D412D436F6E74726963616F2E6A7067, 'O princípio da reparação voluntária.', '”Não semeies mistura em teu horto nem a cultives em teu coração”.', 'Plutão, letra A, número 1.', 'Arrependa-se logo de sua má ação; não se atribule além da conta; console-se virtuosamente.', 'Obtenção de bens com dores morais.', 'Lamentações; lucros insatisfatórios.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (56, 'A Peregrinagem', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F35362D412D506572656772696E6167656D2E6A7067, 'O princípio da própria redenção.', '“Anima e conforta ao atribulado e sustente-se animoso em tuas atribulações”.', 'Lua, letra B, número 2.', 'Longo é o caminho a percorrer; seja forte; aguardam-te aflições.', 'Celibato, timidez.', 'Pessoas relacionadas ao culto divino, penas, perigos misteriosos, inimizades entre pessoas de posição.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (57, 'A Rivalidade', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F35372D412D526976616C69646164652E6A7067, 'O princípio da destreza.', '“Sete vezes cai o justo e, se o é, outras sete volta a se levantar”.', 'Júpiter, letra C, número 3.', 'Respeite o contrário; Junte-se à sã competição; não faça ciladas.', 'Circunstâncias delicadas.', 'Oposições, crítica, debates; o fatal; o cumprimento do destino.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (58, 'A Recapacitação', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F35382D412D5265636170616369746163616F2E6A7067, 'O princípio da conjectura por exame.', '“Se teu próximo te deixa confuso, consulta o caso com teu próprio coração”.', 'Urano, letra CH, número 4.', 'Avalie os argumentos; não desanime; a decisão te pertence.', 'desejo de fazer, valor para sofrer.', 'Aviso de perigos; perdas de posição; reprovações; esperanças falidas.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (59, 'A Revolução', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F35392D412D526576656C6163616F2E6A7067, 'O princípio de manifestação.', '“Os metais se provam pelo fogo e a valia do homem pela boca de quem o elogia ou o censura”.', 'Mercúrio, letra D, número 5.', 'Seja franco; as palavras veladas te trarão prejuízos; fale com clareza.', 'Declarações, autorizações, mensagens, viagens.', 'Notícias contraditórias; adversidade. Violação de correspondência.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (60, 'A Evolução', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F36302D412D45766F6C7563616F2E6A7067, 'O princípio da metamorfose.', '“O que planta e o que rega são uma mesma coisa para a semente”.', 'Vênus, letra E, número 6.', 'Olhe com atenção o futuro; busque a perfeição, mas não seja perfeccionista.', 'Mudanças, quase sempre, adversas.', 'Sucessos dolorosos; reveses de fortuna; falecimentos; perigo de ruína por ideias fixas.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (61, 'A Solidão', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F36312D412D536F6C6964616F2E6A7067, 'O princípio do isolamento.', '“Paga o que deves: ao que deves tributo, tributo; ao que deves afeto, afeto; ao que deves honra, honra”.', 'Netuno, letra F, número 7.', 'Antes de condenar, medite; a solidão é boa conselheira, mas nem sempre.', 'Reserva, economia, precaução, boa conduta.', 'Retraimento voluntário ou forçoso da vida social.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (62, 'A Proscrição', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F36322D412D50726F7363726963616F2E6A7067, 'O princípio da incompatibilidade.', '“O que guarda a língua, sua alma guarda”.', 'Saturno, letra G, número 8.', 'Cuide de sua saúde; resista ao nocivo; busque espaços abertos.', 'Intermediário amável; ausências.', 'Rupturas, divisões, antagonismos, aversões, controvérsias, empresas embaraçosas.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (63, 'A Comunhão', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F36332D412D436F6D756E68616F2E6A7067, 'O princípio da unidade na diversidade.', '“Dá semente ao que quer semear e não a tem, e conselho ao que quer acertar e não sabe”.', 'Marte, letra H, número 9.', 'Compartilhe sua alegria; viva a dor dos demais; não se aniquile com egoísmos.', 'Afetos, ternura, intimidade, afinidade; proteção mútua; luta por ideais comuns.', 'Frieza afetiva; reconciliação posposta.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (64, 'A Veemência', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F36342D412D5665656D656E6369612E6A7067, 'O princípio do fogo interior.', '“Fortaleza é a sabedoria, e pujança, o desejo que a move”.', 'Sol, letra A, número 1.', 'Desejo amoroso cumprido; prazer caprichoso; paixão exaltada.', 'Homem moreno; empresa que requer luta; êxito em assunto amoroso.', 'Paixão extrema; cólera, animosidade.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (65, 'A Aprendizagem', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F36352D412D417072656E64697A6167656D2E6A7067, 'O princípio da própria disciplina.', '“Quem acrescenta ciência, dor acrescenta, de dia ou de noite, para seu bem”.', 'Lua, letra J, número 2.', 'Ninguém é esperto de tudo, porém, sempre há um guia oportuno.', 'Mulher morena; ascenso; proteção de pessoa maior ou mais rica.', 'Descenso; a dita se posterga para mais adiante.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (66, 'A Perplexidade', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F36362D412D506572706C657869646164652E6A7067, 'O princípio de daro devido valor nas decisões.', ' “O que madure as pedras, trabalho terá nisso; e o que cortar a lenha, ao cortá-la, correrá perigo”', 'Júpiter, letra K, número 3.', 'Ninguém é esperto de tudo, porém, sempre há um guia oportuno.', 'Riqueza pelo apoio de dama influente; segurança limitada.', 'Vacilação; assunto em suspenso; proteção condicional.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (67, 'A Amizade', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F36372D412D416D697A6164652E6A7067, 'O princípio do amor puro.', ' “Mão pronta a dar; pé apressado para cumprir, convosco estou na ladeira ou na planície”.', 'Urano, letra L, número 4.', 'afetos cristalinos; contradições passageiras; contatos amáveis e amenos.', 'Jovem moreno; dever cumprido; placidez de ânimo; triunfo sobre obstáculos; apoio de amizades; afetos devotos.', 'Deslize que traz tribulação.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (68, 'A Especulação', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F36382D412D4573706563756C6163616F2E6A7067, 'O princípio da laboriosidade dirigida.', '“Vejam, teus olhos, sem temor, e tuas mãos, com amor”.', 'Mercúrio, letra LL, número 5.', 'Invista já; não esbanje; cuide suas admissões da cobiça de um falso amigo.', 'Trabalho inteligente; aprendizagem proveitosa; abundância de bens materiais; generosidade, liberalidade; notícia favorável sobre assuntos monetários; cálculo adequado no que se pretende.', 'Perda de utilidades; prósperos ganhos evaporam-se; amigos interessados só ajudam a confundir.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (69, 'O Azar', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F36392D4F2D417A61722E6A7067, 'O princípio das faculdades primárias como guia no caminho da vida.', '“Em todo labor há fruto e, em todo fruto, um labor”.', 'Vênus, letra M, número 6.', 'Possíveis dívidas de jogo; litígios com fiadores; fortuna variável.', 'Retribuição, compensação; mudança satisfatória; oportunidade de ascenso; êxito em propriedade imóvel; atrações fortuitas.', 'A firma de um contrato proveitoso se posterga.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (70, 'A Cooperação', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F37302D412D436F6F7065726163616F2E6A7067, 'O princípio da reciprocidade.', '“Não há homem sem ciência nem ciência sem homem”.', 'Netuno, letra N, número 7.', 'Ajuda de um desconhecido; facilita-se a solução do problema; contato proveitoso.', 'Afeto sincero, promessa cumprida, progresso por aplicação de artes ou ciência. Jovem Ruiva.', 'Prosperidade por falecimento ou causa misteriosa; desvanecem-se esperanças sem fundamento.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (71, 'A Avareza', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F37312D412D41766172657A612E6A7067, 'O princípio da prevenção.', '“Cobiçosa é a cobiça, farta em misérias e abundante em dissabores”.', 'Saturno, letra Ñ, número 8.', 'O egoísmo alheio posterga uma recompensa; confusões; acumulo de escritos sem valor.', 'Dificuldades para conservar o posto, ou posição.', 'Remordimento; perigo de roubo; usura.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (72, 'A Purificação', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F37322D412D507572696669636163616F2E6A7067, 'O princípio da própria liberação.', '“Ouve a doutrina de teu pai e não desdenhes o caminho de tua mãe”.', 'Marte, letra O, número 9.', 'Existem sãos propósitos a seu respeito; não creia nas adulações; seu ideal pode concretizar-se.', 'Ingenuidade; compra afortunada; notícia oportuna.', 'Aflição; fortuna após muito esforço laborioso.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (73, 'O Amor e o Desejo', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F37332D4F2D416D6F722D652D4F2D446573656A6F2E6A7067, 'O princípio da ciência natural.', '“Lançadeira de meu tear, tece a tela que há de me abrigar”.', 'Sol, letra P, número 1.', 'Excessos eróticos; incidentes por um terceiro comedido; apegos que não duram.', 'Amante; ilusões; paixão ardente; esperança que depende da vontade do sexo oposto; bens inesperados.', 'Bens que se podem perder por interferência de falsos amigos. Pessoa desordenada.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (74, 'A Oferenda', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F37342D412D4F666572656E64612E6A7067, 'O princípio do amor reverente.', '“Oferenda de flor de farinha no melhor de teus pratos, seja tua dádiva ao coração aflito”.', 'Lua, letra Q, número 2.', 'Obséquio deslumbrante; íntimo regozijo; festa.', 'Devoção; adoração; paixão amorosa; harmonia; inspiração.', 'Discórdia, mais dissipação que conduta edificante.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (75, 'A Generosidade', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F37352D412D47656E65726F7369646164652E6A7067, 'O princípio da abnegação.', '“Frutos suaves ao paladar, tão doces ao receber como ao dar”.', 'Júpiter, letra R, número 3.', 'Chegam boas notícias; aparecem velhas amizades; liberalidade de costumes um pouco perigosos.', 'Pessoa elevada; dádivas, herança, recompensa; aquisição de riqueza; fortuna estável.', 'Bebê.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (76, 'O Dispensador', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F37362D4F2D44697370656E7361646F722E6A7067, 'O princípio da humana providência.', '“Achega-te, estrangeiro, e come de nosso pão e do molho dos meus”.', 'Urano, letra S, número 4.', 'Pedinte agradecido; sentimentos hostis de um parente; misericórdia divina.', 'Importância, celebridade, generosidade, nascimento; fortuna por empresa bem dirigida; trabalho abundante.', 'Gênio variável, constância desfalecente.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (77, 'A Desorientação', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F37372D412D4465736F7269656E746163616F2E6A7067, 'O princípio da associação das ideias.', '“Caminhos pede o pé diligente; ferramentas, a mão laboriosa”.', 'Mercúrio, letra T, número 5.', 'Sua confusão é passageira; manhã prazerosa; noite entre soçobros.', 'Probabilidades de boa fortuna, segundo a própria inspiração.', 'Perplexidade nas decisões; confusão de ideias; obstáculo imprevisto.');
INSERT INTO enochian_db.arcane_minor (id, name, image, moderator_attribute, transcendent_axiom, various_associations, in_general, favorable, not_favorable) VALUES (78, 'O Renascimento', 0x68747470733A2F2F64316765376B65373132637639732E636C6F756466726F6E742E6E65742F37382D4F2D52656E617363696D656E746F2E6A7067, 'O princípio da evolução natural.', '“Dias de sol, noites de lua, graças pela boa ou má fortuna”.', 'Vênus, letra U, número 6.', 'Ascenso, promoção; reconhecem-se os méritos; influências contraditórias de um desconhecido.', 'Extravagância; êxtase; gozo puro; satisfação; contentamento moral; honra e sorte.', 'Logros que só chegam depois das lágrimas.');