INSERT INTO enochian_db.axiom (id, axiom) VALUES (1, 'Sê em tuas obras como és em teus pensamentos.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (2, 'O vento e as ondas vão sempre a favor de quem sabe navegar.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (3, 'Tecendo está teu tear, telas para teu uso e telas que não hás de usar.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (4, 'Ao trabalho de tuas mãos, dá benção e, no do pensamento, põe coração.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (5, 'De ouvidos te havia ouvido, mas agora meus olhos te veem e meu coração te sente.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (6, 'Trabalhos me dás, Senhor, mas com eles, fortaleza.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (7, 'Quando a ciência entre em teu coração e a sabedoria seja doce a tua alma, pede e te será dado.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (8, 'Edifica um altar em teu coração, mas não faças de teu coração um altar.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (9, 'Sobe ao monte e contempla a terra prometida; mas não te digo que entrarás nela.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (10, 'Custoso é o saber que compras com a experiência e mais custoso o que te falta comprar.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (11, 'Gozoso na esperança, sofrido na atribulação, sê constante na oração.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (12, 'Ainda que o sol te fadigue de dia e a lua te entristeça de noite, não leves teus pés ao resvaladeiro nem durmas quando faz guarda.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (13, 'A noite passou e chegou o novo dia. Veste-te, pois, com as armas da luz.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (14, 'Não sejas como palha diante do vento, nem como vento diante da palha.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (15, 'Fizeram-me guarda de vinhas e minha vinha, que era minha, não guardei.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (16, 'Luz de amanhecer, luz de meio-dia, luz de anoitecer, o que importa é que seja luz.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (17, 'Uns homens pedem sinais para crer e outros pedem sabedoria para obrar, mas o coração esperançoso tem tudo em suas esperanças.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (18, 'Seja tua caridade celeiro inesgotável e tua paciência não menos inesgotável que tua caridade.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (19, 'Toma o escudo de tua fé e avança com passo decidido, já seja a favor do vento ou contra todos os ventos.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (20, 'Flor na macieira, fruto na vinha, semeado em madureza.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (21, 'Em seu segredo, não entra minha alma, nem em seu porto meu navio.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (22, 'Sai o sol e põe-se o sol, e outra vez volta a seu lugar, onde torna a nascer.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (23, 'Moa, meu moinho, farinha para mim e farinha para o vizinho.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (24, 'Malha após malha tece meu tear, telas para minha honra e telas para honrar.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (25, 'Navega, minha barca, navega à porfia; navega de noite, navega de dia.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (26, 'Foi tempo de romper e é tempo de coser; tempo foi de falar e é tempo de calar.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (27, 'Nem excesso de mel para adoçar nem vanglória para prosperar.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (28, 'Não busques em outros o que está em ti; nem busques em ti o que está em outros.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (29, 'Olhos de juventude sejam teus olhos; e tua palavra, prudência de ancião seja.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (30, 'Ceifa tua terra com esmero; mas não cultives teu campo com avareza.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (31, 'Não faças juízo sem misericórdia; nem tenhas misericórdia sem juízo.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (32, 'Esgota os recursos de tua inteligência; mas não esgotes os de teu coração.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (33, 'Alegra-te com o amor de tua mocidade; e alegra-te com o amor de tua maturidade.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (34, 'Longevidade há em tua mão direita; e na esquerda, trabalhos e honra.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (35, 'Depois de agora, vem o que foi antes; e antes foi o que será agora.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (36, 'Doce é o tanger, doce o cantar, doce o escutar.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (37, 'Terra de promissão ante meus olhos; ajuda-me, pé, a chegar a ela.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (38, 'Mulher virtuosa, nem todos os que veem suas obras, veem suas virtudes.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (39, 'Põe tuas intenções como testemunho ante ti mesmo; mas, ante os demais, põe como testemunho tuas obras.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (40, 'Não sejas varão de olhos abertos à cobiça; nem mulher de ouvido aberto à lisonja.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (41, 'Os arcos dos fortes foram quebrados; e os débeis cingiram-se de fortaleza.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (42, 'Principal em dignidade, principal em poder, sê principal em mérito.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (43, 'Alegria de coração, embeleza meu rosto.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (44, 'Sobrepuja com sabedoria os receios e goza do que é teu em esperança.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (45, 'Tudo é formoso em seu tempo e tudo é saboroso em sua época.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (46, 'O contentamento no pouco abre as portas do muito.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (47, 'Ser resplendor na luz e calor no fogo é tua missão por agora e para logo.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (48, 'Bebe a gota de tua cisterna, ou os caldais de teu poço.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (49, 'Doce é o trabalho de quem trabalha contente; e doce o descanso de quem o tem merecido.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (50, 'Tição és no incêndio, mulher; e tu, homem, vento que aviva o fogo.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (51, 'Palavra de prata é o conselho do sábio; e palavra de ouro, a de quem o busca e o segue.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (52, 'Põe teu coração em todas as tuas palavras; mas não ponhas todas as palavras em teu coração.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (53, 'Não brandas espada vingadora nem temas espada reparadora.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (54, 'Examina tua vinha e recolhe os grãos caídos; mas não procures rancores nem recolhas insultos.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (55, 'Não semeies mistura em teu horto nem a cultives em teu coração.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (56, 'Anima e conforta ao atribulado e sustente-se animoso em tuas atribulações.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (57, 'Sete vezes cai o justo e, se o é, outras sete volta a se levantar.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (58, 'Se teu próximo te deixa confuso, consulta o caso com teu próprio coração.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (59, 'Os metais se provam pelo fogo e a valia do homem pela boca de quem o elogia ou o censura.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (60, 'O que planta e o que rega são uma mesma coisa para a semente.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (61, 'Paga o que deves: ao que deves tributo, tributo; ao que deves afeto, afeto; ao que deves honra, honra.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (62, 'O que guarda a língua, sua alma guarda.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (63, 'Dá semente ao que quer semear e não a tem, e conselho ao que quer acertar e não sabe.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (64, 'Fortaleza é a sabedoria, e pujança, o desejo que a move.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (65, 'Quem acrescenta ciência, dor acrescenta, de dia ou de noite, para seu bem.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (66, 'O que madure as pedras, trabalho terá nisso; e o que cortar a lenha, ao cortá-la, correrá perigo.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (67, 'Mão pronta a dar; pé apressado para cumprir, convosco estou na ladeira ou na planície.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (68, 'Vejam, teus olhos, sem temor, e tuas mãos, com amor.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (69, 'Em todo labor há fruto e, em todo fruto, um labor.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (70, 'Não há homem sem ciência nem ciência sem homem.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (71, 'Cobiçosa é a cobiça, farta em misérias e abundante em dissabores.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (72, 'Ouve a doutrina de teu pai e não desdenhes o caminho de tua mãe.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (73, 'Lançadeira de meu tear, tece a tela que há de me abrigar.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (74, 'Oferenda de flor de farinha no melhor de teus pratos, seja tua dádiva ao coração aflito.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (75, 'Frutos suaves ao paladar, tão doces ao receber como ao dar.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (76, 'Achega-te, estrangeiro, e come de nosso pão e do molho dos meus.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (77, 'Caminhos pede o pé diligente; ferramentas, a mão laboriosa.');
INSERT INTO enochian_db.axiom (id, axiom) VALUES (78, 'Dias de sol, noites de lua, graças pela boa ou má fortuna.');